BEGIN TRANSACTION;
DROP TABLE "schema_migrations";
CREATE TABLE "schema_migrations" ("version" varchar(255) NOT NULL);
DROP TABLE "comments";
CREATE TABLE "comments" ("id" SERIAL PRIMARY KEY NOT NULL, "author_name" varchar(255), "body" text, "article_id" integer, "created_at" timestamp, "updated_at" timestamp);
DROP TABLE "tags";
CREATE TABLE "tags" ("id" SERIAL PRIMARY KEY NOT NULL, "name" varchar(255), "created_at" timestamp, "updated_at" timestamp);
DROP TABLE "taggings";
CREATE TABLE "taggings" ("id" SERIAL PRIMARY KEY NOT NULL, "tag_id" integer, "article_id" integer, "created_at" timestamp, "updated_at" timestamp);
DROP TABLE "authors";
CREATE TABLE "authors" ("id" SERIAL PRIMARY KEY NOT NULL, "username" varchar(255) NOT NULL, "email" varchar(255) NOT NULL, "crypted_password" varchar(255) NOT NULL, "salt" varchar(255) NOT NULL, "created_at" timestamp, "updated_at" timestamp);
DROP TABLE "articles";
CREATE TABLE "articles" ("id" SERIAL PRIMARY KEY NOT NULL, "title" varchar(255), "body" text, "created_at" timestamp, "updated_at" timestamp, "image_file_name" varchar(255), "image_content_type" varchar(255), "image_file_size" integer, "image_updated_at" timestamp, "author_id" integer, "view_count" integer DEFAULT 0);
CREATE UNIQUE INDEX "unique_schema_migrations" ON "schema_migrations" ("version");
CREATE INDEX "index_comments_on_article_id" ON "comments" ("article_id");
CREATE INDEX "index_taggings_on_tag_id" ON "taggings" ("tag_id");
CREATE INDEX "index_taggings_on_article_id" ON "taggings" ("article_id");
CREATE UNIQUE INDEX "index_authors_on_email" ON "authors" ("email");
COMMIT;
